// Map distro IDs to logo components
const DISTRO_LOGOS: Record<string, React.ComponentType<DistroLogoProps>> = {
  // Ubuntu variants
  ubuntu: UbuntuLogo,
  'ubuntu-24.04': UbuntuLogo,
  'ubuntu-22.04': UbuntuLogo,
  'ubuntu-20.04': UbuntuLogo,
  'Ubuntu': UbuntuLogo,
  'Ubuntu-24.04': UbuntuLogo,
  'Ubuntu-22.04': UbuntuLogo,
  'Ubuntu-20.04': UbuntuLogo,
  'Ubuntu-18.04': UbuntuLogo,
  // Debian
  debian: DebianLogo,
  'Debian': DebianLogo,
  // Fedora
  fedora: FedoraLogo,
  'fedora-latest': FedoraLogo,
  'Fedora': FedoraLogo,
  // Arch
  arch: ArchLogo,
  archlinux: ArchLogo,
  'Arch': ArchLogo,
  // Alpine
  alpine: AlpineLogo,
  'alpine-latest': AlpineLogo,
  'Alpine': AlpineLogo,
  // openSUSE / SUSE
  opensuse: OpenSuseLogo,
  'openSUSE-Tumbleweed': OpenSuseLogo,
  'openSUSE-Leap-15.6': OpenSuseLogo,
  'openSUSE-Leap-15.5': OpenSuseLogo,
  'SUSE-Linux-Enterprise-15-SP5': OpenSuseLogo,
  'SUSE-Linux-Enterprise-15-SP6': OpenSuseLogo,
  suse: OpenSuseLogo,
  // Kali
  kali: KaliLogo,
  'kali-linux': KaliLogo,
  'kali-rolling': KaliLogo,
  // Manjaro
  manjaro: ManjaroLogo,
  'Manjaro': ManjaroLogo,
  // NixOS
  nixos: NixOSLogo,
  'NixOS': NixOSLogo,
  // Linux Mint
  mint: MintLogo,
  'linuxmint': MintLogo,
  'Linux Mint': MintLogo,
  // Rocky Linux
  rocky: RockyLogo,
  'rocky-9': RockyLogo,
  'Rocky': RockyLogo,
  // AlmaLinux
  almalinux: AlmaLogo,
  'almalinux-9': AlmaLogo,
  'AlmaLinux': AlmaLogo,
  // Oracle Linux
  oracle: OracleLogo,
  'OracleLinux_9_1': OracleLogo,
  'OracleLinux_8_7': OracleLogo,
  'OracleLinux_7_9': OracleLogo,
  // CentOS
  centos: CentOSLogo,
  'centos-stream': CentOSLogo,
  'CentOS': CentOSLogo,
  // Gentoo
  gentoo: GentooLogo,
  'Gentoo': GentooLogo,
  // Void Linux
  void: VoidLogo,
  'Void': VoidLogo,
  // Docker / Container
  docker: DockerLogo,
  container: DockerLogo,
};

// Get logo component for a distro ID
export function getDistroLogo(distroId: string): React.ComponentType<DistroLogoProps> {
  // Try exact match first
  if (DISTRO_LOGOS[distroId]) {
    return DISTRO_LOGOS[distroId];
  }

  // Try lowercase
  const lowerDistro = distroId.toLowerCase();
  if (DISTRO_LOGOS[lowerDistro]) {
    return DISTRO_LOGOS[lowerDistro];
  }

  // Try to match by prefix/contains
  for (const [key, Logo] of Object.entries(DISTRO_LOGOS)) {
    if (lowerDistro.includes(key.toLowerCase()) || key.toLowerCase().includes(lowerDistro)) {
      return Logo;
    }
  }

  // Default to generic Linux logo
  return LinuxLogo;
}
